ass="form-control" >			  <option value="">Select Status</option>			  <option value="approved" <?=(isset($filters['filter_status']) && $filters['filter_status'] == 'approved')?'selected':''?>>Approved</option>			  <option value="denied" <?=(isset($filters['filter_status'])  && $filters['filter_status'] == 'denied')?'selected':''?>>Denied</option>			  <option value="pending" <?=(isset($filters['filter_status'])  && $filters['filter_status'] == 'pending')?'selected':''?>>Pending</option>			 </select>	  </div>	 	 		  <div class="col-sm-2">			<button type="submit" id="button-filter"  class="btn btn-primary"><i class="fa fa-filter"></i> Filter</button>		 </div>		</div>		</form>	</div>  		<div class="table-responsive">	<table class="table table-striped table-bordered">		<thead>			<tr>				<th scope="col">#</th>				<th scope="col">Store Owner Name</th>				<th scope="col">Mobile</th>				<th scope="col">Guest Name</th>				<th scope="col">Date Booking</th>				<th scope="col">No. of Guest</th>				<th scope="col">Guest Mobile</th>				<th scope="col">Staus</th>				<th scope="col">Action</th>			</tr>		</thead>		<tbody>			<?php 			$i= $start+1;			if(isset($table_booking) && count($table_booking)>0){			 foreach($table_booking as $table){									$status ='';				$st = $table->status;				if($st==1)				{					$status ='<span class="badge badge-success">Approved</span>';				}else if($st==3)				{					$status ='<span class="badge badge-danger">Denied</span>';				}else if($st==0)				{					$status ='<span class="badge badge-primary">Pending</span>';				}							 ?>				<tr>					<th scope="row"><?php echo $i; ?></th>					<td><?=$table->m[0]->name?></td>					<td><?=$table->m[0]->mobile?></td>					<td><?=$table->name?></td>					<td><?=date('d-M-Y',strtotime($table->booking_date)).'@'.date('h:i a',strtotime($table->booking_time));?></td>					<td><?=$table->number_guest?></td>					<td><?=$table->mobile?></td>					<td class="status_<?php ec